import React, { useRef } from "react";
import clamp from "lodash-es/clamp";
import { useSprings, animated } from "react-spring";
import { useGesture } from "react-use-gesture";
import "./Memories.css";
import i1 from "../image/memory1.jpg";
import i2 from "../image/memory2.jpg";

const pages = [i1, i2];

function ViewPicture() {
  const index = useRef(0);

  const [props, set] = useSprings(pages.length, (i) => ({
    x: i * 450,
    sc: 1,
    display: "block",
  }));
  const a = this.props.width;
  console.log(a.width);

  const bind = useGesture(
    ({ down, delta: [xDelta], direction: [xDir], distance, cancel }) => {
      if (down && distance > 450 / 3)
        cancel(
          (index.current = clamp(
            index.current + (xDir > 0 ? -1 : 1),
            0,
            pages.length - 1
          ))
        );
      set((i) => {
        if (i < index.current - 1 || i > index.current + 1)
          return { display: "none" };
        const x = (i - index.current) * 450 + (down ? xDelta : 0);
        const sc = down ? 1 - distance / 450 / 3 : 1;
        return { x, sc, display: "block" };
      });
    }
  );
  return props.map(({ x, display, sc }, i) => (
    <animated.div
      {...bind()}
      key={i}
      style={{
        display,
        transform: x.interpolate((x) => `translate3d(${x}px,0,0)`),
      }}
    >
      <animated.div
        style={{
          transform: sc.interpolate((s) => `scale(${s})`),
          backgroundImage: `url(${pages[i]})`,
        }}
      />
    </animated.div>
  ));
  //   (
  //     <>
  //       <div
  //         style={{
  //           height: "95%",
  //           width: "95%",
  //           display: "flex",
  //           justifyContent: "center",
  //           alignItems: "center",
  //           fontFamily: "Dancing",
  //           borderRadius: "25px",
  //           boxShadow: "3px 6px 10px 10px #585858",
  //         }}
  //       ></div>
  //     </>
  //   );
}
export default ViewPicture;
