import React  from 'react'
import './Heart.css'

let hearts = [{id:0, url: require('../image/1.png').default},{id:1,url:require('../image/2.png').default}];
let speed = Math.floor(Math.random()*5) + 17;
let left = Math.floor(Math.random()*100);


var tags =<img src={hearts[1].url} className = "Heart" alt = "Heart" 
        style = {mystyle}/>
class Heart extends React.Component {
  constructor(props){
    super(props);
    this.state= {
      random: Math.floor(Math.random()*10)%2,
      left: Math.floor(Math.random()*100)
    };
  }
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    this.setState({
      random: Math.floor(Math.random()*10)%2
    })
    
  }

  render(){
    console.log(this.state.random)
    let mystyle = {
      left: left + '%',
    }
    var tags = (
      <div>
      {tags}
      <img src={hearts[this.state.random].url} className = "Heart" alt = "Heart" 
         style = {mystyle}/>
        </div>
    )
    return (
      tags
    );
  }
}
  
  export default Heart;
