import React from "react";
import Wave from "react-wavify";
import { SwatchesPicker } from "react-color";
import { Menu, Dropdown } from "antd";
import "antd/dist/antd.css";

class ShowDays extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showPicker: false,
      color: "#e69915",
    };
  }
  onClickItems(key) {
    console.log(key);

    if (key === "1") {
      console.log(key);
      this.setState({
        showPicker: !this.state.showPicker,
      });
    }
  }
  handleClose = () => {
    this.setState({ showPicker: false });
  };

  handleChange = (color) => {
    this.setState({ color: color.target.value });
  };

  render() {
    var percent;
    if (this.props.days > 1000) {
      percent = this.props.days / 10000;
    } else {
      percent = this.props.days / 1000;
    }
    percent = Math.floor(250 - percent * 250);
    return (
      <>
        <Dropdown
          overlay={
            <Menu onClick={(key) => this.onClickItems(key)}>
              <Menu.Item key="1">Change color</Menu.Item>
              <Menu.Item key="2">2nd menu item</Menu.Item>
              <Menu.Item key="3">3rd menu item</Menu.Item>
            </Menu>
          }
          trigger={["click"]}
        >
          <div
            className="site-dropdown-context-menu"
            style={{
              width: "250px",
              position: "relative",
              marginBottom: "25px",
              fontSize: "24px",
              fontWeight: "bold",
              color: "white",
              cursor: "pointer",
            }}
          >
            <svg viewBox="0 0 125 125">
              <mask id="myMask">
                <rect x="0" y="0" width="125" height="125" fill="white" />
                <path
                  d="M3,62.5 A20,20,0,0,1,122,62.5 A20,20,0,0,1,3,62.5 Z"
                  fill="black"
                />
              </mask>
              <circle
                fill={this.state.color}
                cx="62.5"
                cy="62.5"
                r="62.5"
                mask="url(#myMask)"
              />
            </svg>
            <Wave
              style={{
                height: "100%",
                position: "absolute",
                left: 0,
                opacity: 0.2,
              }}
              fill={this.state.color}
              mask="url(#mask)"
              options={{ height: percent, points: 4, speed: 1, amplitude: 12 }}
            >
              <mask id="mask">
                <path
                  d="M0,125 A20,20,0,0,1,250,125
                                    M0,125 A20,20,0,0,0,250,125 z"
                  fill="white"
                />
              </mask>
            </Wave>
            <Wave
              style={{
                height: "100%",
                position: "absolute",
                left: 0,
                opacity: 0.3,
              }}
              fill={this.state.color}
              mask="url(#mask)"
              options={{ height: percent, points: 4, speed: 1, amplitude: 7 }}
            >
              <mask id="mask">
                <path
                  d="M0,125 A20,20,0,0,1,250,125
                                    M0,125 A20,20,0,0,0,250,125 z"
                  fill="white"
                />
              </mask>
            </Wave>
            <div
              style={{
                position: "absolute",
                top: "65px",
                width: "100%",
              }}
            >
              In love
            </div>
            <div
              style={{
                position: "absolute",
                top: "105px",
                width: "100%",
              }}
            >
              {this.props.days}
            </div>
            <div
              style={{
                position: "absolute",
                top: "145px",
                width: "100%",
                fontFamily: "Pinyon",
              }}
            >
              Days
            </div>
            {this.state.showPicker ? (
              <div
                style={{
                  position: "absolute",
                  zIndex: "3",
                }}
              >
                <div
                  style={{
                    position: "fixed",
                    top: "0px",
                    right: "0px",
                    bottom: "0px",
                    left: "0px",
                  }}
                  onClick={() => this.handleClose()}
                />
                <SwatchesPicker
                  color={this.state.color}
                  onChange={() => this.handleChange()}
                />
              </div>
            ) : null}
          </div>
        </Dropdown>
      </>
    );
  }
}

export default ShowDays;
