import './App.css';
import BackgroundHeart from './Components/BackgroundHeart'
import Clock from './Components/Clock'
import React from 'react'
import Question from './Components/Question';
import Love from './Components/Love'
import ParticlesBg from 'particles-bg'
import icon from "./Components/icon";
import Background from './image/background.jpg'
import {Row, Col} from 'antd'

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      page : 0,
    }
  }

  OnChangeState(){
    this.setState({page : 1})
  }

  render(){
    let config = {
      num: [4, 7],
      rps: 0.1,
      radius: [5, 40],
      life: [1.5, 3],
      v: [2, 3],
      tha: [-50, 50],
      alpha: [1, 0],
      scale: [.1, 0.9],
      body: icon,
      position: "all",
      //color: ["random", "#ff0000"],
      cross: "dead",
      random: 10
    };
    return (

      <div className="App">
        <header className="App-header">

          {(this.state.page === 0) && ( 
            <>
              <BackgroundHeart/>
              <Question parentCallback = {() => this.OnChangeState()}></Question>
           </>
          )}

          {(this.state.page !== 0) && (
            <>
              <div style={{width: '100%',height: '100%',position:'absolute'}}>
                <ParticlesBg type="custom" config={config} bg={false} />
              </div>
              <Row style={{width:'100%',height:'100%'}}>
                <Col span={8} style={{background:'blue'}}>

                </Col>
                <Col span={8} style={{background:'red'}}>
                  <Love></Love>
                </Col>
                <Col span={8} style={{background:'green'}}>

                </Col>
              </Row>

            </>
          )}
        </header>
      </div>
    );
  }
}

export default App;
