import logo from './image/background_love1.png';
import './App.css';
import BackgroundHeart from './Components/BackgroundHeart'
import Clock from './Components/Clock'
import NoButton from './Components/NoButton'
import React from 'react'
var audio
class App extends React.Component {
  state = {
    play: false
  }
  audio = new Audio('./music/Perfect.mp3');

  componentDidMount() {
    audio.addEventListener('ended', () => this.setState({ play: false }));
  }

  componentWillUnmount() {
    audio.removeEventListener('ended', () => this.setState({ play: false }));  
  }

  togglePlay = () => {
    this.setState({ play: !this.state.play }, () => {
      this.state.play ? this.audio.play() : this.audio.pause();
    });
  }
  render(){
  return (
    <div className="App" onPlay={()=>this.togglePlay()}>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        {/* <Clock></Clock> */}
        <BackgroundHeart/>
        <div className = "question"> Will you be my girlfriend? </div>
        <NoButton/>

      </header>
    </div>
  );
  }
}

export default App;
