import React, {useEffect}  from 'react'
import { render } from 'react-dom';
import './Heart.css'

let hearts = [{id:0, url: require('../image/1.png').default},{id:1,url:require('../image/2.png').default}];
let speed = Math.floor(Math.random()*5) + 17;
let left = Math.floor(Math.random()*100);
let mystyle = {
  left: left + '%',
}

var tags
class Heart extends React.Component {
  constructor(props){
    super(props);
    this.state= {
      random: 0,
    }
  }
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    this.setState({
      random: Math.floor(Math.random()*10)%2
    })
    
  }

  render(){
    tags += <img src={hearts[random].url} className = "Heart" alt = "Heart" 
        style = {mystyle}/>
    return (
      {tags}
    );
  }
}
  
  export default Heart;
