import React  from 'react'
import './NoButton.css'

class NoButton extends React.Component {
  constructor(props){
    super(props);
    this.state= {
      hover: false
    };
  }
  
  toggleHover(){
    this.setState({hover: !this.state.hover})
  }
  render(){
    var x = Math.floor(Math.random()*100)
    var y = Math.floor(Math.random()*100)
    var style = {
        left: x + '%',
        top: y + '%'
    }
    console.log(style);
    return (
      <div className="No" onMouseEnter={()=>this.toggleHover} onMouseLeave={()=>this.toggleHover} style={style}>
          No
      </div>
    );
  }
}
  
  export default NoButton;
