import React  from 'react'
import Heart from './Heart'

const mydiv = <div></div>

class BackgroundHeart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: 0,
    };
  }
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    this.setState({
      id: this.id++,
    });
  }
  render(){
    return (
    {mydiv}
    );
    }
}
  
  export default BackgroundHeart;
