import React from "react";
import { Row, Col } from "antd";
import Avatar1 from "../image/avatar1.jpg";
import Avatar2 from "../image/avatar2.jpg";
import "antd/dist/antd.css";
import { HeartFilled } from "@ant-design/icons";
import "./Avatar.css";
import Male from "../image/male.png";
import Female from "../image/female.png";

class Avatar extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <>
        <Row style={{ margin: "40px 0 0 0" }}>
          <Col span={8}>
            <div style={{ float: "right", padding: "10px 0px" }}>
              <img
                style={{
                  width: "90px",
                  height: "90px",
                  borderRadius: "50%",
                  objectFit: "cover",
                  border: "4px #f3c568 solid",
                  marginBottom: "5px",
                }}
                src={Avatar1}
                alt="avatar"
              ></img>
              <div>Thành Quý</div>
              <div>
                <img src={Male} alt="gender" style={{ display: "flex" }}></img>
              </div>
            </div>
          </Col>
          <Col span={8}>
            <div class="HeartBeat" style={{ width: "100%", height: "100%" }}>
              <HeartFilled
                style={{
                  color: "#f3c568",
                }}
              />
            </div>
          </Col>
          <Col span={8}>
            <div style={{ float: "left", padding: "10px 0px" }}>
              <img
                style={{
                  width: "90px",
                  height: "90px",
                  borderRadius: "50%",
                  objectFit: "cover",
                  border: "4px #f3c568 solid",
                  marginBottom: "5px",
                }}
                src={Avatar2}
                alt="avatar"
              ></img>
              <div>Tố Ngọc</div>
              <div>
                <img
                  src={Female}
                  alt="gender"
                  style={{ display: "flex" }}
                ></img>
              </div>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default Avatar;
