import React from "react";
import { Row, Col } from "antd";
import Avatar1 from "../image/avatar1.jpg";
import Avatar2 from "../image/avatar2.jpg";
import "antd/dist/antd.css";
import { HeartFilled } from "@ant-design/icons";
import "./Avatar.css";
import Male from "../image/male.png";
import Female from "../image/female.png";

class Avatar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showLeftBorderPicker: false,
      showRightBorderPicker: false,
      showLeftTextPicker: false,
      showRightTextPicker: false,
      showCenterPicker: false,
      showHeart: false,
      hearColor: "#03a9f4",
      leftBorderColor: "#03a9f4",
      rightBorderColor: "#03a9f4",
      leftTextColor: "#fff99f",
      rightTextColor: "#ffffff",
    };
  }

  onClickItems = (key) => {
    if (key.key === "1") {
      this.setState({
        showLeftBorderPicker: !this.state.showLeftBorderPicker,
        showRightBorderPicker: false,
        showLeftTextPicker: false,
        showRightTextPicker: false,
        showHeart: false,
      });
    }
    if (key.key === "2") {
      this.setState({
        showLeftBorderPicker: false,
        showRightBorderPicker: false,
        showLeftTextPicker: !this.state.showLeftTextPicker,
        showRightTextPicker: false,
        showHeart: false,
      });
    }
    if (key.key === "3") {
      this.setState({
        showLeftBorderPicker: false,
        showRightBorderPicker: !this.state.showRightBorderPicker,
        showLeftTextPicker: false,
        showRightTextPicker: false,
        showHeart: false,
      });
    }
    if (key.key === "4") {
      this.setState({
        showLeftBorderPicker: false,
        showRightBorderPicker: false,
        showLeftTextPicker: false,
        showRightTextPicker: !this.state.showRightTextPicker,
        showHeart: false,
      });
    }
    if (key.key === "5") {
      this.setState({
        showLeftBorderPicker: false,
        showRightBorderPicker: false,
        showLeftTextPicker: false,
        showRightTextPicker: false,
        showHeart: !this.state.heart,
      });
    }
  };

  handleClose = () => {
    this.setState({
      showLeftPicker: false,
      showRightPicker: false,
    });
  };

  render() {
    return (
      <>
        <Row style={{ margin: "40px 0 0 0" }}>
          <Col span={8}>
            <div style={{ float: "right", padding: "10px 0px" }}>
              <img
                style={{
                  width: "90px",
                  height: "90px",
                  borderRadius: "50%",
                  objectFit: "cover",
                  border: `4px ${this.state.leftBorderColor} solid`,
                  marginBottom: "5px",
                }}
                src={Avatar1}
                alt="avatar"
              ></img>
              <div
                style={{ fontSize: "20px", color: this.state.leftTextColor }}
              >
                Thành Quý
              </div>
              <div style={{ display: "flex" }}>
                <img
                  src={Male}
                  alt="gender"
                  style={{ width: "18px", height: "18px", fontSize: "15px" }}
                ></img>
                <div style={{ color: this.state.leftTextColor }}>
                  11/05/1999
                </div>
              </div>
            </div>
          </Col>
          <Col span={8}>
            <div
              className="HeartBeat"
              style={{ width: "100%", height: "100%" }}
            >
              <HeartFilled
                style={{
                  color: this.state.hearColor,
                }}
              />
            </div>
          </Col>
          <Col span={8}>
            <div style={{ float: "left", padding: "10px 0px" }}>
              <img
                style={{
                  width: "90px",
                  height: "90px",
                  borderRadius: "50%",
                  objectFit: "cover",
                  border: `4px ${this.state.rightBorderColor} solid`,
                  marginBottom: "5px",
                }}
                src={Avatar2}
                alt="avatar"
              ></img>
              <div
                style={{ fontSize: "20px", color: this.state.rightTextColor }}
              >
                Tố Ngọc
              </div>
              <div style={{ display: "flex" }}>
                <img
                  src={Female}
                  alt="gender"
                  style={{ width: "18px", height: "18px", fontSize: "15px" }}
                ></img>
                <div style={{ color: this.state.rightTextColor }}>
                  14/07/1999
                </div>
              </div>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default Avatar;
