import i1 from "../image/memory1.jpg";
import i2 from "../image/memory2.jpg";
import i3 from "../image/memory3.jpg";
import i4 from "../image/memory4.jpg";
import React, { useState } from "react";
import "./Memories.css";
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
} from "reactstrap";

const items = [
  {
    src: i1,
    altText: "Slide 1",
  },
  {
    src: i4,
    altText: "Slide 2",
  },
  //   {
  //     src: i2,
  //     altText: "Slide 2",
  //   },
  //   {
  //     src: i3,
  //     altText: "Slide 3",
  //   },
];

const Memories = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };

  const slides = items.map((item) => {
    return (
      <CarouselItem
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}
        key={item.src}
      >
        <img
          src={item.src}
          alt={item.altText}
          style={{ width: "100%", height: "100%" }}
        />
        {/* <CarouselCaption
          captionText={item.caption}
          captionHeader={item.caption}
        /> */}
      </CarouselItem>
    );
  });

  return (
    <>
      <div
        style={{
          position: "absolute",
          fontSize: "40px",
          zIndex: 1,
          fontFamily: "Dancing",
          top: "10px",
        }}
      >
        Memories
      </div>
      <div
        style={{
          height: "95%",
          width: "95%",
          justifyContent: "center",
          alignItems: "center",
          fontFamily: "Dancing",
          borderRadius: "25px",
          boxShadow: "3px 6px 10px 10px #585858",
          overflow: "hidden",
          backgroundColor: "0c2b2b40",
        }}
      >
        <Carousel
          activeIndex={activeIndex}
          next={next}
          previous={previous}
          className="custom-tag1"
        >
          <CarouselIndicators
            items={items}
            activeIndex={activeIndex}
            onClickHandler={goToIndex}
          />
          {slides}
          <CarouselControl
            direction="prev"
            directionText="Previous"
            onClickHandler={previous}
          />
          <CarouselControl
            direction="next"
            directionText="Next"
            onClickHandler={next}
          />
        </Carousel>
      </div>
    </>
  );
};

export default Memories;
