import "./App.css";
import BackgroundHeart from "./Components/BackgroundHeart";
import React from "react";
import Question from "./Components/Question";
import Love from "./Components/Love";
import ParticlesBg from "particles-bg";
import icon from "./Components/icon";
import Background from "./image/background.jpg";
import { Row, Col } from "antd";
import "antd/dist/antd.css";
import { Menu, Dropdown } from "antd";
const menu = (
  <Menu>
    <Menu.Item key="1">1st menu item</Menu.Item>
    <Menu.Item key="2">2nd menu item</Menu.Item>
    <Menu.Item key="3">3rd menu item</Menu.Item>
  </Menu>
);

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      page: 0,
    };
  }

  OnChangeState() {
    this.setState({ page: 1 });
  }

  render() {
    let config = {
      num: [4, 7],
      rps: 0.1,
      radius: [5, 40],
      life: [1.5, 3],
      v: [2, 3],
      tha: [-50, 50],
      alpha: [1, 0],
      scale: [0.1, 0.9],
      body: icon,
      position: "all",
      //color: ["random", "#ff0000"],
      cross: "dead",
      random: 10,
    };
    return (
      <div className="App" style={{ height: "100%" }}>
        {this.state.page === 0 && (
          <>
            <BackgroundHeart />
            <Question parentCallback={() => this.OnChangeState()}></Question>
          </>
        )}

        {this.state.page !== 0 && (
          <>
            <div
              style={{ width: "100%", height: "100%", position: "absolute" }}
            >
              <ParticlesBg type="custom" config={config} bg={false} />
            </div>
            <Row style={{ height: "100%" }}>
              <Col xs={24} sm={24} md={7} lg={8} xl={8}>
                <Dropdown overlay={menu} trigger={["contextMenu"]}>
                  <div
                    className="site-dropdown-context-menu"
                    style={{
                      textAlign: "center",
                      height: 200,
                      lineHeight: "200px",
                    }}
                  >
                    <svg viewBox="0 0 125 125">
                      <mask id="myMask">
                        <rect
                          x="0"
                          y="0"
                          width="125"
                          height="125"
                          fill="white"
                        />
                        <path
                          d="M3,62.5 A20,20,0,0,1,122,62.5 A20,20,0,0,1,3,62.5 Z"
                          fill="black"
                        />
                      </mask>
                      <circle
                        fill={this.state.color}
                        cx="62.5"
                        cy="62.5"
                        r="62.5"
                        mask="url(#myMask)"
                      />
                    </svg>
                    <Wave
                      style={{
                        height: "100%",
                        position: "absolute",
                        left: 0,
                        opacity: 0.2,
                      }}
                      fill={this.state.color}
                      mask="url(#mask)"
                      options={{
                        height: percent,
                        points: 4,
                        speed: 1,
                        amplitude: 12,
                      }}
                    >
                      <mask id="mask">
                        <path
                          d="M0,125 A20,20,0,0,1,250,125
                                    M0,125 A20,20,0,0,0,250,125 z"
                          fill="white"
                        />
                      </mask>
                    </Wave>
                    <Wave
                      style={{
                        height: "100%",
                        position: "absolute",
                        left: 0,
                        opacity: 0.3,
                      }}
                      fill={this.state.color}
                      mask="url(#mask)"
                      options={{
                        height: percent,
                        points: 4,
                        speed: 1,
                        amplitude: 7,
                      }}
                    >
                      <mask id="mask">
                        <path
                          d="M0,125 A20,20,0,0,1,250,125
                                    M0,125 A20,20,0,0,0,250,125 z"
                          fill="white"
                        />
                      </mask>
                    </Wave>
                  </div>
                </Dropdown>
              </Col>
              <Col
                xs={24}
                sm={24}
                md={10}
                lg={8}
                xl={8}
                style={{
                  backgroundImage: `url(${Background})`,
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                  height: "100%",
                  zIndex: -1,
                }}
              >
                <Love></Love>
              </Col>
              <Col xs={24} sm={24} md={7} lg={8} xl={8}></Col>
            </Row>
          </>
        )}
      </div>
    );
  }
}

export default App;
