import React from "react";
import "./NoButton.css";
import BrokenHeart from "../image/brokenHeart.png";

var x = 55;
var y = 40;
var mystyle;

class NoButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hover: false,
    };
  }

  toggleHover() {
    x = Math.floor(Math.random() * 97);
    y = Math.floor(Math.random() * 97);
    mystyle = {
      left: x + "%",
      top: y + "%",
      position: "absolute",
    };
    console.log(x);
    this.setState({ hover: !this.state.hover });
  }
  render() {
    return (
      <>
        {mystyle ? (
          <button
            className="No"
            onMouseEnter={() => this.toggleHover()}
            onMouseLeave={() => this.toggleHover()}
            onMouseMove={() => this.toggleHover}
            style={mystyle}
          >
            <img
              src={BrokenHeart}
              alt="brokenHeart"
              style={{
                width: "14px",
                marginBottom: "4px",
                marginRight: "8px",
              }}
            />
            No
          </button>
        ) : (
          <button
            className="No"
            onMouseEnter={() => this.toggleHover()}
            onMouseLeave={() => this.toggleHover()}
            onMouseMove={() => this.toggleHover}
          >
            <img
              src={BrokenHeart}
              alt="brokenHeart"
              style={{
                width: "14px",
                marginBottom: "4px",
                marginRight: "8px",
              }}
            />
            No
          </button>
        )}
      </>
    );
  }
}

export default NoButton;
