var custom = {
  width : '50px',
  transition: 'margin 10s',
  margin: '0 0 0 0',
  left: '10px',
  top : '-50px',
  position: 'absolute',
  display: 'flex'
}
let hearts = [{id:0, url: require('../image/1.png').default},{id:1,url:require('../image/2.png').default}];
function Heart() {
  
  let random = Math.floor(Math.random()*10)%2;
  
    return (
      <img src={hearts[random].url} className = "Heart" alt = "Heart" style = {custom}/>
    );
}
  
  export default Heart;
