import React from "react";
import ViewPicture from "./ViewPicture";

class Memories extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      width: null,
    };

    this.selectRef = React.createRef();
  }
  componentDidMount() {
    setTimeout(() => {
      const width = this.selectRef.current.clientWidth;

      this.setState(
        {
          width: width,
        },
        () => console.log(this.state.width)
      );
    }, 1);
  }

  render() {
    return (
      <>
        <div
          ref={this.selectRef}
          style={{
            height: "95%",
            width: "95%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            fontFamily: "Dancing",
            borderRadius: "25px",
            boxShadow: "3px 6px 10px 10px #585858",
          }}
        >
          <ViewPicture />
        </div>
      </>
    );
  }
}
export default Memories;
