import React from 'react'
import ShowDays from './ShowDays'

const meet = new Date("02/06/2019")
const time1 =  new Date().toLocaleString();

var total_days= time1.getDate() - meet.getDate();
console.log(total_days);
class Love extends React.Component{
    constructor(props) {
    super(props);
    this.state = {
      time: new Date().toLocaleString(),
      total_days: 0,
      years: 0,
      months: 0,
      days:0,
      hours: 0,
      minutes: 0,
      second: 0,
    };
  }
  
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    this.setState({
      time: new Date().toLocaleString()
    });
  }
    render(){
        return(
            <>
                <ShowDays></ShowDays>
            </>
        )
    }
}

export default Love;
