import React from 'react'
import ShowDays from './ShowDays'

class Love extends React.Component{
    constructor(props) {
    super(props);
    this.state = {
      time: new Date().toLocaleString()
    };
  }
  
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    this.setState({
      time: new Date().toLocaleString()
    });
  }
    render(){
        return(
            <>
                <ShowDays></ShowDays>
            </>
        )
    }
}

export default Love;
