import React  from 'react'
import './Heart.css'

let hearts = [{url: require('../image/1.png').default},{url:require('../image/2.png').default}];
let speed = Math.floor(Math.random()*5) + 17;
let left = Math.floor(Math.random()*100);


class Heart extends React.Component {
  constructor(props){
    super(props);
    this.state= {
      list: [],
    };
  }
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    var random = Math.floor(Math.random()*10)%2
    var left = Math.floor(Math.random()*100)
    var mystyle = {
      left: left + '%',
    }
    this.setState({
      list: this.state.list.push({url:hearts[random].url,style:mystyle})
    })
    
  }

  render(){
    console.log(this.state.list)
    
    const lists = this.state.list.map((list,index) =>{
      return (
        <img src={list[index].url} className = "Heart" alt = "Heart" key={index}
         style = {list[index].mystyle}/>
      )
    })
    console.log(lists)
    return (
      <div>
      {lists}
      </div>
    );
  }
}
  
  export default Heart;
