import React from 'react'
import ShowDays from './ShowDays'
import Moment from 'moment'

const meet = Moment("02/06/2019","MM/DD/YYYY")
console.log(meet)
const time1 =  Moment();
console.log(time1)

console.log(time1.diff(meet,'days')+1)
console.log(time1.diff(meet,'years'))
console.log((time1.diff(meet,'months'))-12)
console.log(time1.diff(meet,'weeks'))

Date.getFormattedDateDiff = function(date1, date2) {
  var b = moment(date1),
      a = moment(date2),
      intervals = ['years','months','weeks','days'],
      out = [];
 
  for(var i=0; i<intervals.length; i++){
      var diff = a.diff(b, intervals[i]);
      b.add(diff, intervals[i]);
      out.push(diff + ' ' + intervals[i]);
  }
  return out.join(', ');
};



class Love extends React.Component{
    constructor(props) {
    super(props);
    this.state = {
      time: new Date().toLocaleString(),
      total_days: 0,
      years: 0,
      months: 0,
      days:0,
      hours: 0,
      minutes: 0,
      second: 0,
    };
  }
  
  componentDidMount() {
    this.intervalID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  tick() {
    this.setState({
      time: new Date().toLocaleString()
    });
  }
    render(){
        return(
            <>
                <ShowDays></ShowDays>
            </>
        )
    }
}

export default Love;
